function widget_load(b,a,d){$(b).attr("onclick","");var c=$(b).findcomment(a);if(!c)return alert("Widget not found");$.get(ltoptions.url+"/getwidget.htm",{id:a,sidebar:d,themename:ltoptions.theme.name,idurl:ltoptions.idurl},function(a){widget_add(b,$(c).replaceComment(a))},"html")}function widget_findcomment(b,a){return $(b).findcomment(a)}function widget_inline(b){var a=$(b).findcomment(!1);if(!a)return alert("Widget not found");widget_add(b,$(a).replaceComment())}
function widget_add(b,a){$(b).data("litepublisher_widget",a);$(b).click(function(a){widget_toggle(this);return!1})}function widget_toggle(b){$(b).data("litepublisher_widget").slideToggle()}
$(document).ready(function(){window.setTimeout(function(){$("*[rel~='inlinewidget']").one("click",function(){widget_inline(this);return!1});var b='<a class="expandwidget" href="">'+lang.widgetlang.expand+"</a>";$(".inlinewidget, .ajaxwidget").each(function(){$(b).appendTo(this).one("click",function(){if($(this).parent().hasClass("inlinewidget"))widget_inline(this);else{var a=$(this).parent().attr("rel").split("-");widget_load(this,a[1],a[2])}return!1}).click(function(){var a=$(this);a.toggleClass("expandwidget colapsewidget");
a.text(a.hasClass("expandwidget")?lang.widgetlang.expand:lang.widgetlang.colapse);return!1})});$(".widget-load").one("click",function(){var a=$(this);widget_load(this,a.data("idwidget"),a.data("sidebar"));return!1})},120)});
